int getEmptyBlock(); // 得到一个
int getEmptyList();	 //
fcb* find_dir(fcb* dir,char* dirname); //在dir目录中找dirname文件,有返回他的fcb地址，无返回NULL

int main(){
    time(&now) ;
    localtime_r(&tm_now,&now) ; // 得到时间，不重要

    startSys(); //系统初始化
    while(1){
