        }
        else if(strcmp(cmd,"rm")==0){
        	char filename[80];
            scanf("%s",filename);
            my_rm(filename);
        }
        else if(strcmp(cmd,"open")==0){ 
            char filename[80];
            scanf("%s",filename);
            if(my_open(filename)!=-1){  //进入文件打开目录 	               
                while(1){
                	printf("%s :",currentDir);
